import React, { useState, useEffect } from "react";
import {
  Box,
  Flex,
  Grid,
  Image,
  Text,
  Input,
  Select,
  Button,
  Skeleton,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  useDisclosure,
} from "@chakra-ui/react";

// Sample product data
const allProducts = [
  {
    id: 1,
    title: "سيارة مستعملة - تويوتا كامري 2019",
    price: 15000,
    category: "cars",
    description: "تويوتا كامري بحالة جيدة مع 40,000 ميل.",
    image: "data:image/jpeg;base64,", // Replace with an actual image URL
  },
  {
    id: 2,
    title: "تلفاز ذكي سامسونج 55\"",
    price: 500,
    category: "electronics",
    description: "تلفاز سامسونج ذكي 4K بحجم 55 بوصة.",
    image: "data:image/jpeg;base64,", // Replace with an actual image URL
  },
  {
    id: 3,
    title: "مجموعة أرائك حديثة",
    price: 850,
    category: "furniture",
    description: "مجموعة أرائك مريحة وأنيقة تتسع لـ 5 أشخاص.",
    image: "data:image/jpeg;base64,", // Replace with an actual image URL
  },
  {
    id: 4,
    title: "منزل 3 غرف نوم",
    price: 250000,
    category: "real estate",
    description: "منزل واسع في حي هادئ.",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSmVY3wue3dY2Dw_2Kigw3Ql8V2JUrOY1rtAg&s", // Replace with an actual image URL
  },
  {
    id: 5,
    title: "جاكيت جلد رجالي",
    price: 120,
    category: "clothing",
    description: "جاكيت جلد أنيق بحالة ممتازة.",
    image: "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcTVPNCk8cWUxOpvwztW-PrGrfsH9SSfVBpoqb60PwjxXaQ_T8mxIJPwfXr9-f9oKYe81SiicZUIfk9VjnFSoSlsgVp_ePErvOGrk7mCC8Y-8fSJvX1Xjem1&usqp=CAc", // Replace with an actual image URL
  },
  {
    id: 6,
    title: "وظيفة مصمم جرافيك",
    price: 50000,
    category: "jobs",
    description: "وظيفة مصمم جرافيك عن بُعد بدوام كامل.",
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRUR_kIkaGIpM_QsgOZ6MoUdMZBRIo1mZJSIQ&s", // Replace with an actual image URL
  },
];


const Marketplace = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [products, setProducts] = useState(allProducts);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  // Simulate loading state
  useEffect(() => {
    setTimeout(() => setIsLoading(false), 1500); // Simulate an API call delay
  }, []);

  // Handle product click to open modal
  const handleProductClick = (product) => {
    setSelectedProduct(product);
    onOpen();
  };

  // Filter products by search term, category, and price range
  const filterProducts = () => {
    return allProducts
      .filter((product) =>
        product.title.toLowerCase().includes(searchTerm.toLowerCase())
      )
      .filter((product) =>
        selectedCategory ? product.category === selectedCategory : true
      )
      .filter((product) =>(minPrice ? product.price >= minPrice : true))
      .filter((product) =>(maxPrice ? product.price <= maxPrice : true));
  };

  // Handle search, category, and price filter changes
  useEffect(()=>{
    const filteredProducts = filterProducts();
    setProducts(filteredProducts);
  }, [searchTerm, selectedCategory, minPrice, maxPrice]);

  return (
    <Box mt={""}>
      {/* Top Search Bar */}
      <Flex
        bg="white"
        p={4}
        shadow="md"
        position=""
        top="1"
        width="100%"
        color={"black"}
        zIndex={1000}
        alignItems="center"
        justifyContent="space-between"
      >
        <Input
          placeholder="ابحث في السوق..."
          maxW="400px"
          value={searchTerm}
          color={"gray.600"}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Select
          placeholder="التصنيفات"
          maxW="200px"
          ml={4}
          value={selectedCategory}
          color={"gray.600"}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          <option value="cars">سيارات</option>
          <option value="electronics">إلكترونيات</option>
          <option value="furniture">أثاث</option>
          <option value="clothing">ملابس</option>
          <option value="real estate">عقارات</option>
          <option value="jobs">وظائف</option>
        </Select>
        {/* <Button colorScheme="blue" width={"120px"} ml={4} onClick={() => setProducts(filterProducts())}>
          بحث
        </Button> */}
      </Flex>

      {/* Sidebar and Product Grid */}
      <Flex mt="80px">
        {/* Sidebar */}
        <Box w="250px" display={{ base: "none", md: "block" }} p={4} shadow="lg" bg="white">
          <Text fontWeight="bold" mb={4}>
            فلاتر
          </Text>
          <Text color={"gray.600"} textAlign={"center"} fontWeight={"bolder"} fontSize={"18px"} mb={3}>نطاق السعر</Text>
          <Input
            placeholder="أقل سعر"
            mb={2}
            value={minPrice}
            color={"gray.600"}
            onChange={(e) => setMinPrice(e.target.value)}
          />
          <Input
            placeholder="أعلى سعر"
            mb={4}
            color={"gray.600"}
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
          />
          <Button colorScheme="blue" w="100%" onClick={() => setProducts(filterProducts())}>
            تطبيق الفلاتر
          </Button>
        </Box>

        {/* Product Grid */}
        <Box flex="1" p={4}>
          <Grid
            templateColumns={{
              base: "repeat(1, 1fr)",
              sm: "repeat(2, 1fr)",
              md: "repeat(3, 1fr)",
              lg: "repeat(4, 1fr)",
            }}
            gap={6}
          >
            {isLoading ? (
              Array(8)
                .fill(0)
                .map((_, i) => (
                  <Box key={i}>
                    <Skeleton height="200px" />
                    <Skeleton height="20px" mt={2} />
                    <Skeleton height="20px" mt={2} />
                    <Skeleton height="40px" mt={2} />
                  </Box>
                ))
            ) : products.length > 0 ? (
              products.map((product) => (
                <Box
                  key={product.id}
                  borderWidth="1px"
                  borderRadius="lg"
                  overflow="hidden"
                  bg="white"
                  shadow="md"
                  color={"gray.600"}
                  cursor="pointer"
                  transition="transform 0.2s"
                  _hover={{ transform: "scale(1.025)", shadow: "xl" }}
                  onClick={() => handleProductClick(product)}
                >
                  <Image src={product.image} alt={product.title} h="200px" w="100%" objectFit="cover" />
                  <Box p={4}>
                    <Text fontWeight="bold" color={"gray.600"}>{product.title}</Text>
                    <Text color="gray.500">${product.price}</Text>
                    <Text color={"gray.600"} noOfLines={2} mt={2}>
                      {product.description}
                    </Text>
                  </Box>
                </Box>
              ))
            ) : (
              <Text color={"gray.600"} fontWeight={"bolder"} textAlign={"center"}>لم يتم العثور على منتجات</Text>
            )}
          </Grid>
        </Box>
      </Flex>

      {/* Product Detail Modal */}
      <Modal isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader color={"gray.600"}>{selectedProduct?.title}</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <Image src={selectedProduct?.image} alt={selectedProduct?.title} mb={4} />
            <Text fontWeight="bold" color={"gray.600"}>السعر: ${selectedProduct?.price}</Text>
            <Text mt={2} color={"gray.700"}>{selectedProduct?.description}</Text>
          </ModalBody>
          <ModalFooter>
            <Button colorScheme="blue" onClick={onClose}>
              إغلاق
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </Box>
  );
};

export default Marketplace;
