const MemoriesPage = () => {
  const [filterYear, setFilterYear] = useState('');

  // Using Chakra UI color modes for dark/light themes
  const bgColor = useColorModeValue('gray.50', 'gray.900');
  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.800', 'whiteAlpha.900');

  // Filter memories based on the selected year
  const filteredMemories = filterYear
    ? memoriesData.filter((memory) => memory.year === parseInt(filterYear))
    : memoriesData;

  return (
    <Box color={"#222"} maxW="100vw" mx="auto" py={6} px={4} bg={bgColor} minH="100vh">
      <Flex justify="space-between" mb={6} alignItems="center" wrap="wrap">
        <Heading as="h1" size="lg" color={textColor}>
          الذكريات
        </Heading>
        <Select
          placeholder="اختر سنة"
          onChange={(e) => setFilterYear(e.target.value)}
          bg="gray.100"
          width={['100%', '200px']}
        >
          <option value="1">قبل عام</option>
          <option value="2">قبل عامين</option>
          <option value="3">قبل ثلاث سنوات</option>
          <option value="4">قبل أربع سنوات</option>
          <option value="5">قبل خمس سنوات</option>
          {/* Add more filter options */}
        </Select>
      </Flex>

      {/* Memories Feed */}
      <Grid templateColumns={['1fr', 'repeat(2, 1fr)', 'repeat(3, 1fr)']} gap={6}>
        {filteredMemories.map((memory) => (
          <MemoryCard 
              key={memory.id}
              memory={memory}
              bgColor={cardBg} 
              textColor={textColor} 
            />
        ))}
      </Grid>
    </Box>
  );
};

