import React, { useState } from 'react';
import {
  Box,
  Flex,
  Text,
  Image,
  Avatar,
  Button,
  Stack,
  Grid,
  Heading,
  useColorModeValue,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Input,
} from '@chakra-ui/react';
import { FaRegThumbsUp, FaRegCommentDots, FaShare } from 'react-icons/fa';
import { MdLiveTv, MdOutlineNewspaper, MdSportsSoccer } from 'react-icons/md';
import "./news page.css"

const newsData = [
  {
    id: 1,
    type: 'top',
    title: 'الأخبار العاجلة: تظاهرات كبرى في العاصمة',
    description: 'آلاف المواطنين يشاركون في التظاهرات مطالبة بالإصلاحات السياسية...',
    image: 'data:image/jpeg;base64,', // Protest image
    user: 'وكالة الأنباء',
    avatar: 'https://i.pravatar.cc/50?img=1',
    timestamp: 'قبل ساعة',
  },
  {
    id: 2,
    type: 'sports',
    title: 'فوز كبير لفريق المنتخب الوطني',
    description: 'حقق المنتخب الوطني فوزاً مهماً في التصفيات النهائية...',
    image: 'https://images.unsplash.com/photo-1517649763962-0c623066013b?w=600&h=300', // Sports team image
    user: 'قناة الرياضة',
    avatar: 'https://i.pravatar.cc/50?img=2',
    timestamp: 'قبل 4 ساعات',
  },
  {
    id: 3,
    type: 'politics',
    title: 'اجتماع قادة العالم في قمة المناخ',
    description: 'اجتمع قادة العالم لمناقشة التغيرات المناخية وتأثيراتها...',
    image: 'data:image/jpeg;base64,', // Climate change summit
    user: 'تقرير سياسي',
    avatar: 'https://i.pravatar.cc/50?img=3',
    timestamp: 'قبل يوم',
  },
];

const NewsPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('top');

  const bgColor = useColorModeValue('gray.50', 'gray.900');
  const cardBg = useColorModeValue('white', 'gray.700');
  const textColor = useColorModeValue('gray.800', 'whiteAlpha.900');

  const filteredNews = newsData.filter(
    (news) =>
      (filter === 'top' || news.type === filter) &&
      news.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box color={textColor} maxW="100vw" mx="auto" py={6} px={4} bg={bgColor} minH="100vh">
      <Heading as="h1" size="lg" mb={6} textAlign="center" color={textColor}>
        صفحة الأخبار
      </Heading>

      {/* Search and Filter Section */}
      <Flex justify="space-between" mb={6} alignItems="center" wrap="wrap">
        <Input
          placeholder="ابحث في الأخبار"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          width={['100%', '300px']}
          variant="filled"
          bg="gray.100"
          _focus={{ bg: 'white' }}
          mb={[4, 0]}
        />
        <Tabs mt={1} variant="enclosed" colorScheme="blue" isFitted width="100%" bg={bgColor}>
          <TabList>
            <Tab className="tabDetails" onClick={() => setFilter('top')}>
              <MdOutlineNewspaper size="20px" style={{ marginRight: '5px' }} />
              الأخبار العاجلة
            </Tab>
            <Tab className="tabDetails" onClick={() => setFilter('sports')}>
              <MdSportsSoccer size="20px" style={{ marginRight: '5px' }} />
              الرياضة
            </Tab>
            <Tab className="tabDetails" onClick={() => setFilter('politics')}>
              <MdLiveTv size="20px" style={{ marginRight: '5px' }} />
              سياسة
            </Tab>
          </TabList>
        </Tabs>
      </Flex>

      {/* News Feed */}
      <Grid templateColumns={['1fr', 'repeat(2, 1fr)', 'repeat(3, 1fr)']} gap={6}>
        {filteredNews.map((news) => (
          <NewsCard key={news.id} news={news} bgColor={cardBg} textColor={textColor} />
        ))}
      </Grid>
    </Box>
  );
};

const NewsCard = ({ news, bgColor, textColor }) => {
  return (
    <Flex
      direction="column"
      bg={bgColor}
      boxShadow="lg"
      borderRadius="lg"
      p={6}
      transition="all 0.3s"
      _hover={{ boxShadow: '2xl', transform: 'scale(1.05)', bg: 'gray.100' }}
      maxW="100%"
      color={textColor}
    >
      <Image src={news.image} alt={news.title} borderRadius="md" mb={4} />
      <Flex align="center" mb={4}>
        <Avatar src={news.avatar} size="sm" mr={3} mb={3} me={2} />
        <Text fontSize="lg" fontWeight="bold" color={textColor}>
          {news.user}
        </Text>
      </Flex>
      <Text mb={2} fontSize="xl" fontWeight="bold" textAlign="center" color={textColor}>
        {news.title}
      </Text>
      <Text fontSize="sm" textAlign="center" color="gray.500" mb={3}>
        {news.description}
      </Text>
      <Text fontSize="xs" textAlign="center" color="gray.400">
        {news.timestamp}
      </Text>

      <Stack direction="row" mt={4} spacing={4} justify="center">
        <Button colorScheme="gray" leftIcon={<FaRegThumbsUp />} variant="solid">
          إعجاب
        </Button>
        <Button colorScheme="gray" leftIcon={<FaRegCommentDots />} variant="solid">
          تعليق
        </Button>
        <Button colorScheme="gray" leftIcon={<FaShare />} variant="solid">
          مشاركة
        </Button>
      </Stack>
    </Flex>
  );
};

export default NewsPage;
